import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline } from '@mui/material';
import UserForm from './components/UserForm';
import MatchResult from './components/MatchResult';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

function App() {
  const [matchData, setMatchData] = useState(null);

  const handleFormSubmit = (formData) => {
    // In a real application, this would make an API call to find a match
    // For demo purposes, we'll create a mock match
    const mockMatch = {
      name: "John Doe",
      email: "jdoe@rutgers.edu",
      major: formData.major,
    };
    setMatchData({ ...formData, match: mockMatch });
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container component="main" maxWidth="sm" sx={{ pt: 8 }}>
        {!matchData ? (
          <UserForm onSubmit={handleFormSubmit} />
        ) : (
          <MatchResult userData={matchData} />
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
